document.addEventListener('DOMContentLoaded',function(){const scroller=scrollama();function initScrollama(){scroller.setup({step:'.step',offset:0.5,progress:true,debug:false}).onStepEnter(response=>{document.querySelectorAll('.step').forEach(el=>{el.classList.remove('is-active','active')});response.element.classList.add('is-active','active')}).onStepExit(response=>{response.element.classList.remove('is-active','active')})}initScrollama();window.addEventListener('resize',()=>scroller.resize())});